import{_ as t,a as e,b as a,c as o,d as s,e as p,f as i,g as n,h as l,i as c}from"./chunks/about_block.bxaIidlu.js";import{_ as u,c as g,o as _,ag as m}from"./chunks/framework.BgId8OS5.js";const v=JSON.parse('{"title":"Настройка приложения Azure","description":"","frontmatter":{},"headers":[],"relativePath":"ruRU/projbobcat/createNewAzureApp.md","filePath":"ruRU/projbobcat/createNewAzureApp.md","lastUpdated":1770208428000}'),d={name:"ruRU/projbobcat/createNewAzureApp.md"};function A(z,r,h,f,b,D){return _(),g("div",null,r[0]||(r[0]=[m('<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /ruRU/projbobcat/createNewAzureApp.md for this page in Markdown format</div><h1 id="настроика-приложения-azure" tabindex="-1">Настройка приложения Azure <a class="header-anchor" href="#настроика-приложения-azure" aria-label="Permalink to &quot;Настройка приложения Azure&quot;">​</a></h1><nav class="table-of-contents"><ul><li><a href="#настроика-приложения-azure">Настройка приложения Azure</a><ul><li><a href="#начало-настроики">Начало настройки</a></li><li><a href="#просмотр-client-id">Просмотр Client ID</a></li></ul></li></ul></nav><h2 id="начало-настроики" tabindex="-1">Начало настройки <a class="header-anchor" href="#начало-настроики" aria-label="Permalink to &quot;Начало настройки&quot;">​</a></h2><p>Прежде чем начать, вам понадобится учетная запись Microsoft. Регистрация приложения в Azure Active Directory бесплатна.</p><p>Войдите на <a href="https://portal.azure.com/#home" target="_blank" rel="noreferrer">портал Azure</a> с помощью своей учетной записи Microsoft. После входа вы должны увидеть страницу, подобную этой:</p><p><img src="'+t+'" alt="Портал Azure"></p><p>Нажмите <strong>Azure Active Directory</strong> в разделе <strong>Службы Azure</strong>.</p><p><img src="'+e+'" alt="Azure AD"></p><p>Вы увидите страницу, похожую на следующую:</p><p><img src="'+a+'" alt="Портал Azure"></p><p>Затем нажмите <strong>Регистрация приложений</strong> в левой панели функций:</p><p><img src="'+o+'" alt="Портал Azure"></p><p>Нажмите <strong>Новая регистрация</strong> на верхней панели инструментов, введите имя приложения. И выберите <strong>Только личные учетные записи Microsoft</strong> в разделе <strong>Поддерживаемые типы учетных записей</strong>. Поскольку мы не используем аутентификацию на основе обратного вызова URI, вам не нужно заполнять <strong>URI перенаправления</strong> в нижней части формы.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>Пожалуйста, внимательно проверьте информацию в форме, иначе на последующих шагах могут возникнуть непредвиденные проблемы.</p></div><p><img src="'+s+'" alt="Портал Azure"></p><p>Нажмите &quot;Создать&quot; и дождитесь, пока Azure AD завершит создание приложения. После завершения создания веб-страница перенаправит вас на страницу сведений о приложении:</p><p><img src="'+p+'" alt="Портал Azure"></p><p>Затем нажмите <strong>Аутентификация</strong> на левой боковой панели:</p><p><img src="'+i+'" alt="Портал Azure"></p><p>Затем найдите раздел <strong>Дополнительные параметры</strong> справа и убедитесь, что <strong>Поддержка расширенного SDK</strong> и <strong>Разрешить общедоступные клиентские потоки</strong> в этом разделе включены. Если они не включены, включите их вручную.</p><p><img src="'+n+'" alt="Портал Azure"></p><p>Затем нажмите кнопку &quot;Сохранить&quot; ниже и дождитесь завершения сохранения.</p><h2 id="просмотр-client-id" tabindex="-1">Просмотр Client ID <a class="header-anchor" href="#просмотр-client-id" aria-label="Permalink to &quot;Просмотр Client ID&quot;">​</a></h2><p>Поздравляем! Вы завершили регистрацию приложения Azure, теперь вам просто нужно перейти на страницу <strong>Обзор</strong>, чтобы просмотреть Client ID приложения. Нажмите кнопку <strong>Обзор</strong> на левой боковой панели, чтобы просмотреть основную информацию о приложении:</p><p><img src="'+l+'" alt="Портал Azure"></p><p>Справа вы увидите подробную информацию о приложении, найдите <strong>ID приложения (клиента)</strong> в <strong>Сводке</strong>. Этот ID будет Client ID, который вам нужно будет использовать на этапе <a href="/ruRU/projbobcat/installationAndConfig">настройки аутентификатора Microsoft</a>.</p><p><img src="'+c+'" alt="Портал Azure"></p>',28)]))}const N=u(d,[["render",A]]);export{v as __pageData,N as default};
